mapper.select:
SELECT id, pub_set_hash, pub_ext_id, provider_id, ext_id, int_id, version, pub, operator_id, created_at
FROM  mm_m_pub
WHERE pub_ext_id = #{pubExtId}

SELECT mpub.id, mpub.pub_set_hash, mpub.pub_ext_id, mpub.provider_id, mpub.ext_id, mpub.int_id, mpub.version, mpub.pub, mpub.operator_id, mpub.created_at
FROM  mm_m_pub mpub
WHERE mpub.pub_set_hash = #{pubSetHash}

mapper.delete:
DELETE
FROM  mm_m_pub
WHERE provider_id = #{providerId} AND ext_id = #{extId} AND pub = #{publication} AND version = #{version}
