mapper.select:
SELECT id, provider_id, ext_id, int_id, version, pub, operator_id, created_at
FROM  mm_m_pub
WHERE provider_id = #{providerId} AND ext_id = #{extId} AND pub = #{publication} AND version = #{version}

mapper.delete:
DELETE
FROM  mm_m_pub
WHERE provider_id = #{providerId} AND ext_id = #{extId} AND pub = #{publication} AND version = #{version}
