mapper.select:
SELECT mext.id, mext.commit, mext.group, mext.name, mext.tree, mext.owner_id, mext.visibility, mext.begin_time, mext.end_time, mext.operator_id
FROM mm_m_ext mext
WHERE mext.group = #{group}
AND mext.name = #{name}
AND mext.tree = #{tree}
AND mext.owner_id = #{ownerId}

    AND mext.end_time IS NULL


    AND mext.end_time = #{endTime}

SELECT mext.id, mext.commit, mext.group, mext.name, mext.tree, mext.owner_id, mext.visibility, mext.begin_time, mext.end_time, mext.operator_id
FROM mm_m_ext mext
WHERE mext.group = #{group}
AND mext.name = #{name}
AND mext.tree = #{tree}


    AND mext.end_time >= #{endTime}
    ORDER BY mext.begin_time DESC
    LIMIT 0, 1



    AND mext.end_time IS NULL

SELECT mext.id, mext.commit, mext.group, mext.name, mext.tree, mext.owner_id, mext.visibility, mext.begin_time, mext.end_time, mext.operator_id FROM mm_m_ext mext WHERE mext.id = #{id}
AND mext.end_time IS NULL
AND mext.end_time >= #{end_time}
ORDER BY mext.begin_time DESC LIMIT 0, 1


SELECT mext.id, mext.commit, mext.group, mext.name, mext.tree, mext.owner_id, mext.visibility, mext.begin_time, mext.end_time, mext.operator_id
FROM mm_m_ext mext
WHERE mext.owner_id = #{ownerId}
AND mext.group LIKE #{group_pattern}
AND mext.end_time IS NULL

mapper.update:
UPDATE mm_m_ext mext
SET mext.end_time = #{endTime}
WHERE mext.id = #{extId}

mapper.delete:
UPDATE mm_m_ext
SET end_time = #{endTime}
WHERE id = #{id}
AND end_time IS NULL
