mapper.select:

SELECT mext.id, mext.group, mext.name, mext.tree, mext.owner_id, mext.visibility
FROM mm_m_ext mext
WHERE mext.owner_id = #{ownerId}
AND mext.`group` LIKE #{group_pattern}
AND mext.end_time IS NULL

mapper.update:
UPDATE mm_m_ext
SET end_time = #{endTime}
WHERE owner_id = #{ownerId} AND `group` = #{group}
