mapper.select:
SELECT mint.id, mint.ext_id, mint.field, mint.is_single, mint.structure, mint.ref_m_pub_set, mint.visibility, mint.is_required
FROM mm_m_int mint
WHERE mint.ext_id = #{extId}
AND mint.field = #{field}
ORDER BY mint.begin_time DESC
LIMIT 0,1

SELECT mint.id, mint.ext_id, mint.field, mint.is_single, mint.structure, mint.ref_m_pub_set, mint.visibility, mint.is_required
FROM mm_m_int mint
WHERE mint.ext_id = #{extId}
AND mint.field = #{field}
AND mint.end_time IS NULL

SELECT mint.field
FROM mm_m_int mint, mm_m_pub mpub
WHERE mpub.pub_set = #{pubSet}
AND mpub.ext_id = mint.ext_id
AND mpub.int_id = mint.id
AND mint.ext_id = #{extId}

AND mint.begin_time <= #{beginTime}
AND ((mint.end_time >= #{beginTime}) OR mint.end_time IS NULL)

GROUP BY mint.field

mapper.update:
UPDATE mm_m_int mint
SET mint.end_time = #{endTime}
WHERE mint.id = #{id}
