mapper.select:
SELECT mpub.id, mpub.pub_set_hash, mpub.provider_id, mpub.ext_id, mpub.int_id, mpub.version, mpub.stability, mpub.operator_id, mpub.begin_time
FROM  mm_m_pub mpub
WHERE mpub.pub_set_hash = #{pubSet}

mapper.delete:
DELETE
FROM  mm_m_pub
WHERE provider_id = #{providerId} AND ext_id = #{extId} AND stability = #{stability} AND version = #{version}
