mapper.select:
SELECT count(msub.id) FROM mm_m_sub msub
WHERE msub.sub_set = #{subSet} AND msub.subscriber_id = #{subscriberId}
AND msub.group = #{group} AND msub.name = #{name} AND msub.tree = #{tree}
AND msub.end_time IS NULL


SELECT msub.id, msub.sub_set, msub.subscriber_id, msub.`group`, msub.name, msub.tree, msub.operator_id, msub.begin_time, msub.end_time
FROM mm_m_sub msub
WHERE msub.subscriber_id = #{ownerId}
AND msub.group LIKE #{group_pattern}
AND msub.end_time IS NULL

SELECT msub.id, msub.subscriber_id, msub.group, msub.name, msub.tree, msub.begin_time, msub.end_time,
mext.id root_ext_id, mpub.pub_set, mpub.provider_id
FROM mm_m_pub mpub, mm_m_sub msub, mm_m_ext mext
WHERE mext.id = mpub.ext_id
AND msub.sub_set = mpub.pub_set
AND msub.group = #{group}
AND msub.name = #{name}
AND msub.tree = #{tree}
AND msub.end_time IS NULL
GROUP BY mext.id

SELECT mext.id, mpub.pub_set, mpub.provider_id, mext.group, mext.name, mpub.stability, mpub.version, mpub.begin_time, mpub.end_time
FROM mm_m_pub mpub, mm_m_sub msub, mm_m_ext mext
WHERE mext.id = mpub.ext_id
AND mpub.provider_id = #{providerId}
AND mpub.pub_set = #{pubSet}
GROUP BY mext.id


SELECT msub.subscriber_id FROM mm_m_sub msub
WHERE subscriber_id LIKE #{subscriberId_pattern}
GROUP BY msub.subscriber_id

SELECT msub.id, msub.sub_set, msub.subscriber_id, msub.`group`, msub.name, msub.tree, msub.operator_id, msub.begin_time, msub.end_time
FROM mm_m_sub msub
WHERE msub.subscriber_id = #{ownerId}
AND msub.group = #{group}
AND msub.name = #{name}
AND msub.tree = #{tree}
AND msub.end_time IS NULL

SELECT msub.id, msub.sub_set, msub.subscriber_id, msub.`group`, msub.name, msub.tree, msub.operator_id, msub.begin_time, msub.end_time
FROM mm_m_sub msub
WHERE msub.id = #{id}
WHERE msub.subscriber_id = #{subscriberId}
AND msub.end_time IS NULL
