mapper.select:
SELECT count(msub.id) FROM mm_m_sub msub
WHERE msub.sub_set_hash = #{subSet} AND msub.subscriber_id = #{subscriberId}
AND msub.group = #{group} AND msub.name = #{name}
AND msub.end_time IS NULL


SELECT msub.id, msub.sub_set_hash, msub.subscriber_id, msub.`group`, msub.name, msub.tree, msub.operator_id
FROM mm_m_sub msub
WHERE msub.subscriber_id = #{ownerId}
AND msub.group LIKE #{group_pattern}
AND msub.end_time IS NULL
