mapper.select:
SELECT ssub.pub_set, ssub.provider_id, ssub.visibility, ssub.m_sub_id, ssub.stability, ssub.version, msub.group, msub.name
FROM km_s_pub ssub, mm_m_sub msub
WHERE ssub.provider_id = #{providerId}
AND ssub.m_sub_id = msub.id
AND msub.group = #{group}
AND msub.name = #{name}
AND ssub.stability = #{stability}
AND ssub.version = #{version}
AND ssub.end_time IS NULL
GROUP BY ssub.pub_set
