mapper.select:
SELECT mins.id, mins.commit, mins.owner_id, mins.sub_id, mins.ext_id, mins.int_id, mins.field, mins.value ,mins.value_set, mins.value_ref_id, mins.value_ref_policy, mins.operator_id, mins.begin_time, mins.end_time
FROM km_i_ins mins, km_m_sub msub
WHERE msub.id = #{subId}
AND msub.subscriber_id = #{subscriberId}
AND mins.sub_id = msub.id



    AND mins.begin_time <= #{beginTime}
    AND ((mins.end_time >= #{endTime}) OR (mins.end_time IS NULL))




    AND mins.begin_time <= #{endTime}
    AND ((mins.end_time >= #{endTime}) OR (mins.end_time IS NULL))




    AND mins.begin_time >= #{beginTime}
    AND ((mins.end_time >= #{beginTime}) OR (mins.end_time IS NULL))

SELECT mins.id, mins.commit, mins.owner_id, mins.sub_id, mins.ext_id, mins.int_id, mins.field, mins.value ,mins.value_set, mins.value_ref_id, mins.value_ref_policy, mins.operator_id, mins.begin_time, mins.end_time
FROM km_i_ins mins
WHERE mins.sub_id = #{subId}
AND mins.int_id = #{intId}
AND mins.end_time IS NULL

mapper.update:
UPDATE km_i_ins mins
SET mins.end_time = #{endTime}
WHERE mins.sub_id = #{subId} AND mins.int_id = #{intId} AND mins.end_time IS NULL
