mapper.update:
UPDATE km_s_pub spub
SET spub.end_time = #{endTime}
WHERE spub.end_time IS NULL
AND spub.provider_id = #{providerId}
AND spub.pub_set = #{pubSet}

mapper.select:

SELECT msub.id, msub.sub_set, msub.subscriber_id, msub.group, msub.name, msub.tree, msub.begin_time, msub.end_time
FROM mm_m_sub msub
WHERE msub.subscriber_id = #{ownerId}
AND msub.group LIKE #{group_pattern}
AND msub.end_time IS NULL
GROUP BY msub.sub_set
